jQuery(window).load(function(){  
    if(checkBrowser() == 'Google Chrome'){
        if (window.addEventListener) window.addEventListener('DOMMouseScroll', wheel, false);
            window.onmousewheel = document.onmousewheel = wheel;

            var time = 330;
            var distance = 100;

            function wheel(event) {
                if (event.wheelDelta) delta = event.wheelDelta / 60;
                else if (event.detail) delta = -event.detail / 3;
                handle();
                if (event.preventDefault) event.preventDefault();
                event.returnValue = false;
            }

            function handle() {
                jQuery('html, body').stop().animate({
                    scrollTop: jQuery(window).scrollTop() - (distance * delta)
                }, time);
            }
    }
});
function checkBrowser(){
    var ua = navigator.userAgent;
    
    if (ua.search(/MSIE/) > 0) return 'Internet Explorer';
    if (ua.search(/Firefox/) > 0) return 'Firefox';
    if (ua.search(/Opera/) > 0) return 'Opera';
    if (ua.search(/Chrome/) > 0) return 'Google Chrome';
    if (ua.search(/Safari/) > 0) return 'Safari';
    if (ua.search(/Konqueror/) > 0) return 'Konqueror';
    if (ua.search(/Iceweasel/) > 0) return 'Debian Iceweasel';
    if (ua.search(/SeaMonkey/) > 0) return 'SeaMonkey';
    if (ua.search(/Gecko/) > 0) return 'Gecko';

    return 'Search Bot';
}
(function($){
	$.fn.tmStickUp=function(options){ 
		
		var getOptions = {
			correctionSelector: $('.correctionSelector')
		}
		$.extend(getOptions, options); 

		var
			_this = $(this)
		,	_window = $(window)
		,	_document = $(document)
		,	thisOffsetTop = 0
		,	thisOuterHeight = 0
		,	thisMarginTop = 0
		,	thisPaddingTop = 0
		,	documentScroll = 0
		,	pseudoBlock
		,	lastScrollValue = 0
		,	scrollDir = ''
		,	tmpScrolled
		;

		init();
		function init(){
			thisOffsetTop = parseInt(_this.offset().top);
			thisMarginTop = parseInt(_this.css("margin-top"));
			thisOuterHeight = parseInt(_this.outerHeight(true));

			$('<div class="pseudoStickyBlock"></div>').insertAfter(_this);
			pseudoBlock = $('.pseudoStickyBlock');
			pseudoBlock.css({"position":"relative", "display":"block"});
			addEventsFunction();
		}//end init

		function addEventsFunction(){
			_document.on('scroll', function() {
				tmpScrolled = $(this).scrollTop();
					if (tmpScrolled > lastScrollValue){
						scrollDir = 'down';
					} else {
						scrollDir = 'up';
					}
				lastScrollValue = tmpScrolled;

				correctionValue = getOptions.correctionSelector.outerHeight(true);
				documentScroll = parseInt(_window.scrollTop());

				if(thisOffsetTop - correctionValue < documentScroll){
					_this.addClass('isStuck');
					_this.css({position:"fixed", top:correctionValue});
					pseudoBlock.css({"height":thisOuterHeight, "display":"block"});
					if(jQuery(window).width() > 991){
						jQuery(".block-cart-header").css({"display":"none"});
					};
				}else{
					_this.removeClass('isStuck');
					_this.css({position:"relative", top:0});
					pseudoBlock.css({"height":thisOuterHeight, "display":"none"});
					if(jQuery(window).width() > 991){
						jQuery(".block-cart-header").css({"display":"block"});
					};
				}
			}).trigger('scroll');
		}
	}//end tmStickUp function
})(jQuery)
